mapper.select:
select

from message where id in(
select max(id) from message
where status != 2
and from_id != 1
and (from_id = #{userId} or to_id = #{userId})
group by conversation_id
)
order by id desc
limit #{offset}, #{limit}

select count(id)
from message
where status = 0
and from_id != 1
and to_id = #{userId}

    and conversation_id = #{conversationId}
